//gsap loader
const tl = gsap.timeline({
    duration: 4
});

function preLoader(tl) {
    tl.to('.preLoader.black > img', {
        delay: 1,
        y: 50,
        autoAlpha: 0
    })
        .to('.preLoader.black', {
            yPercent: -100
        })
        .to('.preLoader.white', {
            yPercent: -100
        })
        .to('.preLoader', {
            css: {
                display: 'none'
            }
        })

        .from('.topar ul li', {
            x: -50,
            autoAlpha: 0,
            stagger: 0.05
        })
        .from(
            '.topar select', {
            autoAlpha: 0,
            x: 50,
            stagger: 0.05
        },
            '>-0.5'
        )

        .from('.navbar-brand > img', {
            x: -50,
            autoAlpha: 0
        })

        .from(
            '.navbar-nav > li, .form-inline', {
            autoAlpha: 0,
            x: 100,
            stagger: 0.05
        },
            '>-0.5'
        )

        .from('.slideOne', {
            autoAlpha: 0,
            yPercent: 100,
            stagger: 0.05
        })
        .from('figure.slideimg', {
            autoAlpha: 0,
            yPercent: 100,
            stagger: 0.05
        });


    // .from(
    //  '.bg-image',
    //  {
    //      autoAlpha: 0
    //  },
    //  '<-0.25'
    // )
}

